package operators;

public class PatternProgrameExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 5; i++) {// define row column first loop

			for (int j = 0; j <= i; j++) {// define row second loop

				System.out.print("*");// print star pattren

			}
			
			System.out.println(" ");// define using space pattern
		}
	}

}
